mapper.select:
select

from ds_geo_style
where data_status = '1' and del_flag = '1'

    and style_id = #{styleId,jdbcType=INTEGER}


      and style_name like '%' || #{styleName,jdbcType=VARCHAR} || '%'


      and style_name_cn like '%' || #{styleNameCn,jdbcType=VARCHAR} || '%'


      and workspace = #{workspace,jdbcType=VARCHAR}

  order by


      ${sidx} ${sord}


      modify_time desc
