mapper.insert:

insert into clt_task_user (id, data_status, del_flag, 
  create_time, creator_name, creator, 
  modify_time, modifier_name, modifier, 
  remark, task_id, account_id, is_shoulder
  )
values (#{id,jdbcType=INTEGER}, #{dataStatus,jdbcType=CHAR}, #{delFlag,jdbcType=CHAR}, 
  #{createTime,jdbcType=TIMESTAMP}, #{creatorName,jdbcType=VARCHAR}, #{creator,jdbcType=INTEGER},
  #{modifyTime,jdbcType=TIMESTAMP}, #{modifierName,jdbcType=VARCHAR}, #{modifier,jdbcType=INTEGER},
  #{remark,jdbcType=VARCHAR}, #{taskId,jdbcType=INTEGER}, #{accountId,jdbcType=INTEGER} , #{isShoulder,jdbcType=CHAR}
  )

mapper.select:
select

from clt_task_user
where task_id = #{taskId,jdbcType=INTEGER}
and creator = #{creator,jdbcType=INTEGER}
and creator != account_id
