mapper.select:
select

from clt_template
where 1=1

  AND template_name like CONCAT('%',#{templateName,jdbcType=VARCHAR},'%')


  AND purpose like CONCAT('%',#{purpose,jdbcType=VARCHAR},'%')


  and type = #{type,jdbcType=INTEGER}


  and data_status = #{dataStatus,jdbcType=CHAR}


  and del_flag = #{delFlag,jdbcType=CHAR}


  and create_time = #{createTime,jdbcType=DATE}


  AND creator_name like CONCAT('%',#{creatorName,jdbcType=VARCHAR},'%')


  and creator = #{creator,jdbcType=INTEGER}


  and modify_time = #{modifyTime,jdbcType=DATE}


  and modifier_name = #{modifierName,jdbcType=VARCHAR}


  and modifier = #{modifier,jdbcType=INTEGER}


  and remark = #{remark,jdbcType=VARCHAR}

ORDER BY


    ${sidx} ${sord}


    type desc,create_time DESC

select
count(1)
from clt_template
where 1=1

  AND template_name = #{templateName,jdbcType=VARCHAR}


  AND template_id != #{templateId,jdbcType=INTEGER}
